import React, {useState, useEffect} from 'react';

export const Product = (props) => {
    const { nombre, precio, descripcion, img1 } = props.data;
    //new
    
       
    return (
        <div className="product">
            <div className="slide-var">
                <ul>
                    <li><img src={`data:image;base64,${img1}`} alt={nombre}/></li>
                    <li><img src={`../imagenes/productos/pro1.png`} alt={nombre}/></li>
                </ul>
            </div>
            <div className="descripcion">
                <p>{descripcion}</p>
            </div>
            <div className="description"> 
                <p> <b>{nombre}</b></p>
                <p> ${precio}</p>
                <p className="Boton">Comprar</p>
            </div>
        </div> 
    );
};
