import React, { useState } from "react";
import Axios from "axios";
import Swal from 'sweetalert2';
import 'bootstrap/dist/css/bootstrap.min.css';
import ButtonToolbar from 'react-bootstrap/ButtonToolbar';


function EditarPerfiles(){
    const [nombre, setNombre]=useState("");
    const [users, setUsers] = useState([]);
    const [password, setPassword]=useState("");
    const [email, setEmail]=useState("");
    const [telefono, setTelefono]=useState('');


    //Filtro de perfiles
    const [TipoFiltro, setTipoFiltro]=useState("");
    const [ValorFiltro, setValorFiltro]=useState("");

    const [empleadosList,setEmpleados] =useState([]);
    const [editar, setEditar]=useState(false);
    const [filtro, setFiltro]=useState(false);

    /*const RegistrarNuevoUsuario = () =>{
        if(nombre && password && email && telefono){   
            //getUsuarios();
           Axios.post("http://localhost:3001/createUsers", {
                nombre: nombre, 
                password: password, 
                telefono: telefono, 
                email: email, 
                users: email
            }).then(()=>{
                alert("Usuario registrado");
               
            });
        }
    }*/

    const RegistrarNuevoUsuario = () =>{
        Axios.post('http://localhost:3001/createUsers', {
            nombre: nombre, 
            password: password, 
            telefono: telefono, 
            email: email, 
            users: email
        }).then(()=>{
            alert("Usuario registrado");
            getUsuarios();
        });
    }

    const getUsuarios = () =>{
        Axios.get("http://localhost:3001/usuarios").then((response)=>{
          setEmpleados(response.data);        
        });
    }

    const update = () =>{
        Axios.put("http://localhost:3001/update", {
            nombre: nombre, 
            password: password, 
            telefono: telefono, 
            email: email, 
            users: email
        }).then(()=>{
            getUsuarios();
            limpiarCampos();
        });
    }

    const limpiarCampos = () =>{
        setNombre("");
        setUsers("");
        setPassword("");
        setEmail("");
        setTelefono('');
    }

    const editarEmpleado = (val) => {
        setEditar(true);
        setNombre(val.nombre);
        setUsers(val.user);
        setPassword(val.password);
        setEmail(val.email);
        setTelefono(val.telefono);
    }

    const eliminarUsuario = (user) =>{
        Axios.delete(`http://localhost:3001/deleteUsuarios/${user}`).then(()=>{
            Swal.fire(`El usuario fue eliminado correctamente`);    
          getUsuarios();
          limpiarCampos();
        });
    }

    const activarFiltro = () =>{
        setFiltro(true);
    }

    return (
        <div className="App">
            <div className='datos'>
                <div>
                    <div className="card header">
                        Crear un nuevo usuario
                    </div>
                    <form className="row g-3">
                        <div className="col-md-4">
                            <label htmlFor="validationDefault01" className="form-label">Nombre:</label>
                            <input onChange={(event) =>{setNombre(event.target.value);}} type='text' value={nombre} className="form-control" id="validationDefault01" required/>
                        </div>
                        <div className="col-md-4">
                            <label htmlFor="validationCustom02" className="form-label">Contraseña:</label>
                            <input onChange={(event) =>{setPassword(event.target.value);}} type='password' value={password} className="form-control" id="validationDefault02" required/>
                        </div>   
                        <div className="col-md-4">
                            <label htmlFor="validationCustom03" className="form-label">Correo:</label>
                            <input onChange={(event) =>{setEmail(event.target.value);}} type='text' value={email} className="form-control" id="validationDefault03" required/>
                        </div>
                        <div className="col-md-4">
                            <label htmlFor="validationCustom03" className="form-label">Correo:</label>
                            <input onChange={(event) =>{setTelefono(event.target.value);}} type='text' value={telefono} className="form-control" id="validationDefault03" required/>
                        </div>
                        <div className="card footer text-body-secondary">
                            <ButtonToolbar aria-label="Toolbar with button groups">
                            {
                                editar?<button className="btn btn-warning" onClick={update}>Actualizar</button>
                                :<button className="btn btn-success" onClick={RegistrarNuevoUsuario}>Registrar</button>
                            }
                            </ButtonToolbar>
                        </div>
                    </form>  
                    <div className="card footer text-body-secondary">
                            <ButtonToolbar aria-label="Toolbar with button groups">
                                <button className="btn btn-info" onClick={getUsuarios}>Listar Empleados</button>
                                <button className="btn btn-warning" onClick={activarFiltro}>Filtros </button>
                            </ButtonToolbar>
                    </div>
                </div>
                <div className='listar'>
                        <table className="table table-striped">
                            <thead>
                                <tr>
                                    <th scope="col">Usuario</th>
                                    <th scope="col">Contraseña</th>
                                    <th scope="col">Email</th>
                                    <th scope="col">Telefono</th>
                                    <th scope="col">Nombre completo</th>
                                    <th scope="col">Acciones</th>
                                </tr>
                            </thead>
                                {
                                    empleadosList.map((val,key)=>{
                                        return <tr>
                                            <th scope="row">{val.user}</th>
                                            <td>{"****"} </td>
                                            <td>{val.email} </td>
                                            <td>{val.telefono} </td>
                                            <td>{val.nombre} </td>
                                            <td>
                                                <button type="button"
                                                onClick={()=> {
                                                editarEmpleado(val)     
                                                }}  
                                                className="btn btn-info">Editar</button>
                                                <button type="button" 
                                                        className="btn btn-danger"
                                                        onClick={()=>{
                                                            eliminarUsuario(val.user)}
                                                        }>Eliminar</button>
                                            </td>
                                        </tr>
                                    })
                                }
                            </table>
                </div>
            </div>
        </div>
    );
};

export default EditarPerfiles;
