import React from 'react';
import { Product } from './product';
import './shop.css';
import axios from 'axios';
import { useState } from 'react';
import { useEffect } from 'react';

const URI = 'http://localhost:3001/read/';

export const Shop = () => {
    const[imageUrl, setImageUrl] = useState('');
    
    const[products,setProducts] = useState([])
    useEffect(() => {
        getProducts()
    }, []);

    const getProducts = async () => {
        const res = await axios.get(URI)
        setProducts(res.data)
    };

    useEffect(() => {
        axios.get('http://localhost:3001/imagenes/1', {
             responseType: 'blob',
            headers: {
        }
    }).then(response => {
        const url = URL.createObjectURL(response.data);
        setImageUrl(url);
    })
    .catch(err => {
      console.log(err)
    })
  },[]);

  const url = 'https://i.sstatic.net/RRuCp.png'

const fetchImage = async url => {
  const response = await fetch(url, {mode: 'no-cors',})
  const blob = await response.blob()
  
  return blob
}

const loadImage = async () => {
  const imageBlob = await fetchImage(url)

  const reader = new FileReader()
  reader.onload = () => {
    const base64data = reader.result               
    console.log({base64data})
  }
  reader.onerror = () => {                
    console.log('error')
  }
  reader.readAsDataURL(imageBlob) 
}

loadImage();

  

    return (
        <div className="shop">
            <div className="shopTitle">
                <h1><spam>T</spam>ienda Virtual</h1>
                <img className="logo" src={require(`../../imagenes/icons/tienda.png`)}/>
            </div>
            <div className="products"> 
                
                    {products.map((product) => (
                        <Product data={product} /> 
                    ))}
                
            </div>
        </div>
    )
};
