import React from "react";
import './login.css';
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { useState, useEffect } from "react";
import { ShopContext } from "../../context/shop-context";
import { useContext } from "react";
import Swal from 'sweetalert2';
import withReactContent from 'sweetalert2-react-content';

const URI = 'http://localhost:3001/usuarios/';

const Login = () => {
    const context = useContext(ShopContext);
    const navigate = useNavigate();
    const navigateRegister = () => {
        navigate(`/register`);
    }

    const navigateLogin = () => {
        navigate(`/login`);
    }

    const navigateShopAddtoCart = () => {
        navigate(`/shop`);
        
    }

    const navigateEditInventory = () => {
        navigate(`/editInventory`);
        //navigate(`Productos`);
    }

    const [entrada, SetEntrada] = useState('');
    const [entradaP, SetEntradaP] = useState('');
    const [users, setUsers] = useState([]);
    
    useEffect(() => {
        getUsers();
    }, [])

    const getUsers = async() => {
        const res = await axios.get(URI)
        setUsers(res.data)
    }

    const mensaje = () => {
        withReactContent(Swal).fire({
            title: <i>Error de usuario</i>,
            input: 'number',
            preConfirm: () => {
              let cant = 0; 
              cant = Swal.getInput()?.value; 
              if (cant===0){
                Swal.fire(`El valor no puede ser cero`);
              }else{
                Swal.fire(`Dato registrado`);
