import React, { useState } from "react";
import Axios from "axios";
import '../hoja-de-estilos/Producto.css';
import Footer from './Footer.jsx';
import Navegacion from './Navegacion';
import Swal from 'sweetalert2';
import withReactContent from 'sweetalert2-react-content';

/* Todo componente debe iniciar con mayusculas*/
function Producto() {
  const [cantidad, setCantidad]=useState(0);
  const [count, setCount] = useState(0);
  const [cart, setCart] = useState([]);

  const [productosList,setProductos] =useState([]);

  
    Axios.get("http://localhost:3001/read").then((response)=>{
      setProductos(response.data);        
    });


  const agregarCarrito = (val) => {
    withReactContent(Swal).fire({
      title: <i>Cantidad</i>,
      input: 'number',
      cantidad,
      preConfirm: () => {
        let cant = 0; 
        cant = Swal.getInput()?.value; 
        if (cant==0){
          Swal.fire(`El valor no puede ser cero`);
        }else{
          Swal.fire(`Dato registrado`);
          setCount(count + val.precio);
          let car = `${val.id} ${val.nombre} $ ${val.precio} cantidad: ${cant}`;
          setCart([...cart, car]);
        }
      },
    })
  }

  const removeFromCart = (val) => {
    withReactContent(Swal).fire({
      title: <i>¿Estas seguro?</i>,
      input: 'select',
      inputOptions: {   
        Y: "Si",
        N: "No",
      },     
      showCancelButton: false,
      inputValidator: (value) => {
      if (value === "Y") {
        Swal.fire(`Eliminado`);
        setCart(cart.filter(item => item.toString() !== val.toString()));
      } else {
        Swal.fire(`Cancelado`);
      };
    }  
    });
  };

  const pagar = () => {
    <a href="Carrito">Carrito</a>
  }

 
  return(
    <div className="contenedor-productos">
      <div className='listar'>
        <p>Conteo: {count}</p> 
        <div className="cart">{
          cart.map((val,key)=>{
            return  <tr>
                      <th scope="row">{val.toString()}</th>
                      <td>
                        <button type="button" className="btn btn-info"  onClick={()=>removeFromCart(val.toString())}>Quitar</button>
                      </td>
                    </tr>
            })
          }
          {count?<button type="button" className="btn btn-info" onClick={()=><a href="Carrito">Carrito</a>}>Pagar</button>
          :null}
      </div>
          
        <table className="table table-striped">
          <thead>
            <tr>
              <th scope="col">id Producto</th>
              <th scope="col">Descripción</th>
              <th scope="col">Valor</th>
              <th scope="col">Acciones</th>
            </tr>
          </thead>
          {
            productosList.map((val,key)=>{
              return  <tr>
                        <th scope="row">{val.id}</th>
                        <td>{val.nombre} </td>
                        <td>{val.precio} </td>
                        <td>
                          <button type="button" className="btn btn-info"  onClick={()=>agregarCarrito(val)}>Agregar</button>
                        </td>
                      </tr>
            })
          }
        </table>
      </div>
    </div>
  );
};

function AppProducto() {
    return (
      <div className="App">
        <Navegacion/>
        <br/><br/>
        <div className="contenedor-productos">
          <Producto/>
        </div>        
        <Footer/>
      </div>
    );
};  


export default AppProducto;

/**
 * 
