import React from "react";
import '../hoja-de-estilos/Testimonio.css';
import Navegacion from './Navegacion';

/* Todo componente debe iniciar con mayusculas*/
function Testimonio(props){
    return(
      <div>
        <Navegacion/>
        <div className="contenedor-testimonio">
            <img className="imagen testimonio" src={require(`../imagenes/t${props.imagen}.jpeg`)}/>
            <div className="contenedor-texto">
                <p className="nombre-testimonio">{props.nombre} en {props.pais} </p>
                <p className="cargo-testimonio"> {props.cargo} en {props.empresa} </p>
                <p className="texto-testimonio"> {props.testimonio} </p>
            </div>
        </div>
      </div>
    );
}

function AppTestimonio(){
    return (
        <div className="App">
          <Testimonio
            imagen = "1"
            nombre = "Xim Juan"
            pais = "Corea del sur"
            cargo = "Ingeniera de Software"
            empresa = "Sistemas Enlinea"
            testimonio = "lorem" 
          />
          <Testimonio
            imagen = "2"
            nombre = "Ema Bostian"
            pais = "Suecia"
            cargo = "Ingeniera de Software"
            empresa = "Sistemas Enlinea"
            testimonio = "lorem" 
          />
          <Testimonio
            imagen = "3"
            nombre = "Ema Bostian"
            pais = "Inglaterra"
            cargo = "Ingeniera de Software"
            empresa = "Sistemas Enlinea"
            testimonio = "lorem" 
          />
        </div>
    )
};

export default AppTestimonio;
