import React, { useState } from "react";
import '../hoja-de-estilos/IngresoDatos.css';
import 'bootstrap/dist/css/bootstrap.min.css';

function GuardarDatos(){
    const [nombre, setNombre]=useState("");
    const [edad, setEdad]=useState(0);
    const [pais, setPais]=useState("");
    const [cargo, setCargo]=useState("");
    const [anios, setAnios]=useState(0);

    const mostrarDatos = () =>{
      alert(nombre);
    }

    return (
        <div className="App">
            <div className='datos'>
                <div className="card text-center">
                <h4>Guardar datos</h4>
                <div class="mb-3 row">
                    <label for="staticEmail" className="col-sm-2 col-form-label">Email</label>
                    <div class="col-sm-10">
                        <input type="text" readonly class="form-control-plaintext" id="staticEmail" value="email@example.com"/>

                        <label >Nombre: <input onChange={(event) =>{setNombre(event.target.value);}} type='text'/></label>
                        <label>Edad: <input onChange={(event) =>{setEdad(event.target.value);}} type='number'/></label>
                        <label>Pais: <input onChange={(event) =>{setPais(event.target.value);}} type='text'/></label>
                        <label>Cargo: <input onChange={(event) =>{setCargo(event.target.value);}} type='text'/></label>
                        <label>AÃ±o: <input onChange={(event) =>{setAnios(event.target.value);}} type='number'/></label>
                    </div>
                </div>
                
                <button className="btn btn-success" onClick={mostrarDatos}>REGISTRAR</button>
                </div>
            </div>
        </div>
    );
};

export default GuardarDatos;
