import React from "react";
import '../hoja-de-estilos/Producto.css';
import Footer from './Footer.jsx';
import Navegacion from './Navegacion';

/* Todo componente debe iniciar con mayusculas*/
function Producto(props) {
  return(
    <div className="contenedor-producto">
        <img className="imagen-producto" src={require(`../imagenes/productos/pro${props.imagen}.png`)}/>
        <div className="contenedor-texto">
            <p className="nombre-producto">{props.nombre}</p>
            <p className="descripcion-producto"> {props.descripcion}</p>
        </div>
    </div>
  );
};

function AppProducto() {
    return (
      <div className="App">
        <Navegacion/>
        <br/><br/>
        <div className="contenedor-productos">
        <Producto
          imagen = "1"
          nombre = "Camisa negra"
          descripcion = "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Donec accumsan orci tincidunt nulla commodo maximus. Nullam ut semper ante, ut."
        />
        <Producto
          imagen = "2"
          nombre = "Camisa Roja"
          descripcion = "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Donec accumsan orci tincidunt nulla commodo maximus. Nullam ut semper ante, ut."
        />
        <Producto
          imagen = "3"
          nombre = "Zapatos Converse"
          descripcion = "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Donec accumsan orci tincidunt nulla commodo maximus. Nullam ut semper ante, ut."
        />
        <Producto
          imagen = "4"
          nombre = "Gafas negras"
          descripcion = "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Donec accumsan orci tincidunt nulla commodo maximus. Nullam ut semper ante, ut."
        />
        </div>
        
        <Footer/>
      </div>
    );
};  


export default AppProducto;
