import React, { useState } from "react";
import Axios from "axios";
import '../hoja-de-estilos/IngresoDatos.css';
import 'bootstrap/dist/css/bootstrap.min.css';

function GuardarDatos(){
    const [nombre, setNombre]=useState("");
    const [edad, setEdad]=useState(0);
    const [pais, setPais]=useState("");
    const [cargo, setCargo]=useState("");
    const [anio, setAnios]=useState(0);
    const [id, setId]=useState(0);

    const [empleadosList,setEmpleados] =useState([]);
    const [editar, setEditar]=useState(false);

    const mostrarDatos = () =>{
      Axios.post("http://localhost:3001/create", {
        nombre:nombre,
        edad:edad,
        pais:pais,
        cargo:cargo,
        anio:anio
      }).then(()=>{
        alert("empresa registrado");
        getEmpleados();
      });
    }

    const getEmpleados = () =>{
        Axios.get("http://localhost:3001/read").then((response)=>{
          setEmpleados(response.data);        
        });
    }

    const update = () =>{
        Axios.put("http://localhost:3001/update", {
          id:id,
          nombre:nombre,
          edad:edad,
          pais:pais,
          cargo:cargo,
          anio:anio,
        }).then(()=>{
          getEmpleados();
          limpiarCampos();
        });
    }

    const limpiarCampos = () =>{
        setNombre("");
        setEdad(0);
        setPais("");
        setCargo("");
        setAnios(0);
        setId(0);
    }

    const editarEmpleado = (val) => {
        setEditar(true);

        setNombre(val.nombre);
        setEdad(val.edad);
        setPais(val.pais);
        setCargo(val.cargo);
        setAnios(val.anio);
        setId(val.id);
    }

    const deleteEmpleado = (val) =>{
        Axios.put("http://localhost:3001/delete", {
          id:id,
        }).then(()=>{
          getEmpleados();
        });
      }

    return (
        <div className="App">
            <div className='datos'>
                <div className="card text-center">
                    <div className="card header">
                        Guardar datos
                    </div>
                    
                    <div className="mb-3 row">
                        <label>Nombre: <input onChange={(event) =>{setNombre(event.target.value);}} type='text' value={nombre}/></label>
                        <label>Edad: <input onChange={(event) =>{setEdad(event.target.value);}} type='number' value={edad}/></label>
                        <label>Pais: <input onChange={(event) =>{setPais(event.target.value);}} type='text' value={pais}/></label>
                        <label>Cargo: <input onChange={(event) =>{setCargo(event.target.value);}} type='text' value={cargo}/></label>
                        <label>Año: <input onChange={(event) =>{setAnios(event.target.value);}} type='number' value={anio}/></label>
                        <button className="btn btn-info" onClick={getEmpleados}>LISTAR</button>
                    </div>
                    <div className="card footer text-body-secondary">
                        {
                            editar?<button className="btn btn-warning" onClick={update}>Actualizar</button>
                            :<button className="btn btn-success" onClick={mostrarDatos}>Registrar</button>
                        }
                        
                    </div>
                    
                </div>
                <div className='listar'>
                        <table className="table table-striped">
                            <thead>
                                <tr>
                                    <th scope="col">ID</th>
                                    <th scope="col">Nombre</th>
                                    <th scope="col">Edad</th>
                                    <th scope="col">Pais</th>
                                    <th scope="col">Cargo</th>
                                    <th scope="col">Año</th>
                                    <th scope="col">Acciones</th>
                                </tr>
                            </thead>
                                {
                                    empleadosList.map((val,key)=>{
                                        return <tr>
                                            <th scope="row">{val.id}</th>
                                            <td>{val.nombre} </td>
                                            <td>{val.edad} </td>
                                            <td>{val.pais} </td>
                                            <td>{val.cargo} </td>
                                            <td>{val.anio} </td>
                                            <td>
                                                <button type="button"
                                                onClick={()=> {
                                                editarEmpleado(val)     
                                                }}  
                                                className="btn btn-info">Editar</button>
                                                <button type="button" 
                                                        className="btn btn-danger"
                                                        onClick={()=>{deleteEmpleado(val.id)}}>Eliminar</button>
                                            </td>
                                        </tr>
                                    })
                                }
                            </table>
                </div>
            </div>
        </div>
    );
};

export default GuardarDatos;
