import React from "react";
import { useNavigate } from "react-router-dom";
import axios from 'axios';
import { useState } from "react";

const URIADMIN = 'http://localhost:3001/updateUsuario/';

const EditPerfiles = () => {

    const [password, setPassword] = useState('');
    const [telefono, setTelefono] = useState('');
    const [nombre, setNombre] = useState('');
    const [user, setUser] = useState('');
    const navigate = useNavigate();

    const navigateShop = () => {
        navigate(`/editInventory`);
    }

    const actualizar = async (e) => {
        e.preventDefault();
        await axios.put(URIADMIN, { password: password,  telefono: telefono, nombre: nombre, user: user });
        navigateShop();
    }


    return (
        <div className="register-form">
            <h2>Editar Perfiles de usuario</h2>
            <form onSubmit={actualizar} action="/auth" method="post">
                <input 
                value={password}
                onChange={ (e) => setPassword((e.target.value))}
                type="password" name="pass" id="pass" placeholder="ContraseÃ±a"/>
                <input 
                value={telefono}
                onChange={ (e) => setTelefono(e.target.value)}
                type="text" name="pass" id="pass" placeholder="Telefono"/>
                <input 
                value={nombre}
                onChange={ (e) => setNombre(e.target.value)}
                type="text" name="pass" id="pass" placeholder="Nombre"/>
                <input type="submit" className="btn-login" value="Editar" />
            </form>
        </div>
    )
}

export default EditPerfiles;
