import React, {useState, useEffect} from 'react';
import axios from "axios";

export const Product = (props) => {
    const { nombre, precio, descripcion, img1 } = props.data;
    //new
    const[imageUrl, setImageUrl] = useState('')

    useEffect(() => {
        axios.get('http://localhost:3001/imagenes/1', {
            headers: {
        }
    }).then(response => {
      console.log(response)
      const url = URL.createObjectURL(response.data);
      setImageUrl(response.data)
      console.log(url)
    })
    .catch(err => {
      console.log(err)
    })
  },[])
    
  console.log(imageUrl);

    return (
        <div className="product">
            <div className="slide-var">
                <ul>
                    <li><img src={`data:image;base64,${img1}`} alt={nombre}/></li>
                    <li><img src={`../imagenes/productos/pro1.png`} alt={nombre}/></li>
                </ul>
            </div>
            <div className="descripcion">
                <p>{descripcion}</p>
            </div>
            <div className="description"> 
                <p> <b>{nombre}</b></p>
                <p> ${precio}</p>
                <p className="Boton">Comprar</p>
            </div>
        </div> 
    );
};
