import React, { useContext } from 'react';
import { CartContext } from '../Contextos/CartContext';
import '../hoja-de-estilos/Carrito.css';

const Cart = () => {
  const { cart, removeFromCart } = useContext(CartContext);

  return (
    <div className="cart">
      <h2>Carrito</h2>
      
    </div>
  );
};

export default Cart;


