import React, { useState } from "react";
import Axios from "axios";
import Swal from "sweetalert2";
import "bootstrap/dist/css/bootstrap.min.css";
import ButtonToolbar from "react-bootstrap/ButtonToolbar";

function EditarPerfiles() {
  const [nombre, setNombre] = useState('');
  const [users, setUsers] = useState([]);
  const [password, setPassword] = useState('');
  const [email, setEmail] = useState('');
  const [telefono, setTelefono] = useState('');

  const [usuariosLista, setUsuarios] = useState([]);
  const [editar, setEditar] = useState(false);

  const RegistrarNuevoUsuario = () => {
    /*Axios.post("http://localhost:3001/createUsers", {
      nombre: nombre,
      password: password,
      telefono: telefono,
      users: email,
    }).then(() => {
      alert("Usuario registrado");
      getUsuarios();
    });*/
    Swal.fire(`El usuario fue creado correctamente`);
    getUsuarios();
  };

  const getUsuarios = () => {
    Axios.get("http://localhost:3001/usuarios").then((response) => {
        setUsuarios(response.data);
    });
  };

  const update = () => {
    console.log("telefono: " + telefono);
    Axios.put("http://localhost:3001/updateUsuario", {
      nombre: nombre,
      password: password,
      telefono: telefono,
      users: email,
    }).then(() => {
      getUsuarios();
      limpiarCampos();
    });
  };

  const limpiarCampos = () => {
    setNombre("");
    setUsers("");
    setPassword("");
    setEmail("");
    setTelefono("");
  };

  const editarEmpleado = (val) => {
    setEditar(true);
    setNombre(val.nombre);
    setUsers(val.user);
    setPassword(val.password);
    setEmail(val.email);
    setTelefono(val.telefono);
  };

  const eliminarUsuario = (user) => {
    Axios.delete(`http://localhost:3001/deleteUsuarios/${user}`).then(() => {
      Swal.fire(`El usuario fue eliminado correctamente`);
      getUsuarios();
      limpiarCampos();
    });
  };

  return (
    <div className="App">
        <div className="datos">
            <div>
                <div className="card header">Crear un nuevo usuario</div>
                    <form className="row g-3">
                        <input value={nombre} onChange={(e) => setNombre(e.target.value)} type="text" placeholder="Nombre completo"/>
                        <input value={password} onChange={(e) => setPassword(e.target.value)} type="password" placeholder="Contraseña"/>
                        <input value={telefono} onChange={(e) => setTelefono(e.target.value)}  type="text" placeholder="telefono"/>
                        <input value={email} onChange={(e) => setEmail(e.target.value)}  type="text" placeholder="Correo electronico"/>
                        <div className="card footer text-body-secondary">
                            {editar ? (
                                <button className="btn btn-warning" onClick={update}>Actualizar</button>
                            ) : (<button className="btn btn-success" onClick={getUsuarios}>Registrar</button>)}
                        </div>
                    </form>
                    <button className="btn btn-success" onClick={getUsuarios}>Registrar</button>
                    <button className="btn btn-info" onClick={getUsuarios}>Listar Empleados</button>
                </div>
            </div>
            <div className="listar">
                <table className="table table-striped">
                    <thead>
                        <tr>
                <th scope="col">Usuario</th>
                <th scope="col">Contraseña</th>
                <th scope="col">Email</th>
                <th scope="col">Telefono</th>
                <th scope="col">Nombre completo</th>
                <th scope="col">Acciones</th>
              </tr>
            </thead>
            {usuariosLista.map((val, key) => {
              return (
                <tr>
                  <th scope="row">{val.user}</th>
                  <td>{"****"} </td>
                  <td>{val.email} </td>
                  <td>{val.telefono} </td>
                  <td>{val.nombre} </td>
                  <td>
                    <button
                      type="button"
                      onClick={() => {
                        editarEmpleado(val);
                      }}
                      className="btn btn-info"
                    >
                      Editar
                    </button>
                    <button
                      type="button"
                      className="btn btn-danger"
                      onClick={() => {
                        eliminarUsuario(val.user);
                      }}
                    >
                      Eliminar
                    </button>
                  </td>
                </tr>
              );
            })}
          </table>
        </div>
      </div>
  );
}

export default EditarPerfiles;
