import React, { createContext, useState } from 'react';
import axios from 'axios';
import { useEffect } from 'react';

export const ShopContext = createContext(null);
const URI = 'http://localhost:3001/read';

const getDefaultCart = () => {
    let cart = {}
    cart[i] = 0
  
    
};

export const ShopContextProvider = (props) => {
    const [cartItems, setCartItems] = useState(getDefaultCart());
    const [payAumount,setPayAumount] = useState(0);

    const[products, setProducts] = useState([])
    useEffect(() => {
        getProducts()
    }, []);

    const [inventario, setInventario] = useState([]);

    const [logged, setLogged] = useState(0);
    const loggedChanger = (value) => setLogged(value);

    const [admin, setAdmin] = useState(false);
    const AdminChanger = (value) => setAdmin(value);
    
    const getProducts = async () => {
        const res = await axios.get(URI)
        setProducts(res.data);

    }

    const getInventario = () => {
        let stock = 0;
        stock = inventario.map(inv => (
            stock = inv.inventario
        ));
        return stock;
    }


    const getTotalCartAmount = () => {
        let totalAmount = 0;
        for (const item in cartItems) {
            if (cartItems[item] > 0) { 
                let itemInfo = products.find((product) => product.id === Number(item));
                totalAmount += cartItems[item] * itemInfo.precio;
            }
        }
 
        return totalAmount;
    };



    /*const addToCart = async (itemId) => { 
        await axios.get(`http://localhost:3001/inventario/${itemId}`)
        .then(({ data }) => {
           setInventario(data);
            let cant = getInventario();
            
               cant> 0 ? setCartItems((prev) => ({...prev, [itemId]: prev[itemId] + 1 })) : void(0);
               cant===0 ? alert('Empty product') : void(0);
               
            //setCartItems((prev) => ({...prev, [itemId]: prev[itemId] + 1 }));
            
        })
        .catch(error => {
            console.log(error.message);
        }) 
    };*/

    const addToCart = async (itemId) => { 
        setCartItems((prev) => ({...prev, [itemId]: prev[itemId] + 1 }))
    };

    const removeFromCart = async (itemId) => { 
        await axios.get(`http://localhost:3001/inventario/${itemId}`)
        .then(({ data }) => {
            //data==='Unbooked' ? setCartItems((prev) => ({...prev, [itemId]: prev[itemId] - 1 })) : void(0);
            setCartItems((prev) => ({...prev, [itemId]: prev[itemId] - 1 }));
        })
        .catch(error => {
            console.log(error.message);
        }) 
    };

    const contextValue = { cartItems, addToCart, removeFromCart, getTotalCartAmount, loggedChanger, logged, AdminChanger, admin, payAumount,setPayAumount};
    return (
        <ShopContext.Provider value={contextValue}>
            {props.children}
        </ShopContext.Provider>
    );
};
